type AutoGenerated struct {
	Services []Service `json:"services"`
}

type Service struct {
	ServiceID  string `json:"service_id"`
	Properties DemoProperties
}

type DemoProperties struct {
	Value   string `json:"value"`
	MsgType string `json:"msgType"`
}

func sendDeviceCommand(deviceId string, client iot.ApplicationClient) {
	request := iot.DeviceSyncCommandRequest{
		ServiceId:   "value",
		CommandName: "SET_DEVICE",
		Paras: struct {
			CmdValue string
		}{CmdValue: "First Command"},
	}

	resp := client.SendDeviceSyncCommand(deviceId, request)
	fmt.Println(*resp)
}
